#include <iostream>

using namespace std;

int main() {
	 int x,y,z,v; // Deklarasi variabel panjang,lebar,tinggi,volume
    string dimensi;

    cout<<"PROGRAM C++ MENGHTUNG VOLUME BALOK"<<endl;
    cout<<"----------------------------------"<<endl;
    //Menginput nilai panjang,lebar dan tinggi
    cout<<"Masukan Panjang : "; 
    cin>>x;
    cout<<"Masukan Lebar   : ";
    cin>>y;
    cout<<"Masukan Tinggi  : ";
    cin>>z;
    cout<<"Masukan Satuan (mm,cm,m) Dimensi Balok : ";
    cin>>dimensi;
    cout<<endl;
    
    v=x*y*z; //Rumus untuk menghitung volume balok
    
    cout<<"Volume Balok    = "<<v<<" "<<dimensi<<"3"; //Tampilkan 
	return 0;
}
